get();
if (JSON.stringify(v) == "{}") $('全部分类');
if(type){
		t = [{name:"　　　　    ༺ˇ»`ʚ  分类选择  ɞ´«ˇ༻   　　　　",type:"button",action:"$('全部分类')"}
,{name:"　 查看分类设置 　",type:"button",action:"look(0)"}
,{name:"　 格式分组名称 　",type:"button",action:"look(1)"}];
		for (key in v) t.push({
			name:key,
			type:"button",
			action:"$('"+key+"')"
		});
