    childNodes: doc.select("#content>div").toArray(),
    load: function() {
        var a = String(java.base64Decoder(java.getString('//*[@name="client"]/@content'))).split(/[A-Z]+%/);
        var j = 0;
        for (var i = 0; i < a.length; i++) {
            if (a[i] < 3) {
                this.childNode[a[i]] = this.childNodes[i + this.star];
                j++
            } else {
                this.childNode[a[i] - j] = this.childNodes[i + this.star];
                j=j+2
            }
        };
    }
}
content.load();
result=chapter+content.childNode.join("<br>");
