 });
function fetchHtml(i, url) {
            nurl = url + "?limit=" + i;
            $.ajax({
                url: nurl,
                dataType: 'json',
                success: function (json) {
                    for (var k = 0; k < json.boards.length; k++) {
                        for (var q = 0; q < json.boards[k].pins.length; q++) {
                            $('#box').append('<li><img src=\"https://gd-hbimg.huaban.com/' + json.boards[k].pins[q].file.key + '\"></li>\n');
                        }
                    }
                    box.viewer.update();
                },
                error: function (xhr) { }
            });
        }
</script>

